-- Create artist_photos table if it doesn't exist
CREATE TABLE IF NOT EXISTS public.artist_photos (
    id uuid DEFAULT gen_random_uuid() PRIMARY KEY,
    artist_id uuid REFERENCES public.artist_profiles(id) ON DELETE CASCADE,
    image_url text NOT NULL,
    caption text,
    display_order integer DEFAULT 0,
    is_featured boolean DEFAULT false,
    created_at timestamp with time zone DEFAULT now(),
    updated_at timestamp with time zone DEFAULT now()
);

-- Enable RLS on artist_photos table
ALTER TABLE public.artist_photos ENABLE ROW LEVEL SECURITY;

-- Create policies for artist_photos
CREATE POLICY "Anyone can view artist photos" 
ON public.artist_photos 
FOR SELECT 
USING (true);

CREATE POLICY "Admins can manage artist photos" 
ON public.artist_photos 
FOR ALL 
USING (has_role(auth.uid(), 'admin'::app_role))
WITH CHECK (has_role(auth.uid(), 'admin'::app_role));